"use strict";
document.addEventListener("DOMContentLoaded",function(){
	[].slice.call(document.querySelectorAll(".section-toggle")).forEach(function(e){
		e.addEventListener("click",function(e){
			var t=e.target.parentNode.parentNode.parentNode,n=t.querySelector(".endpoints");
			n.classList.contains("hidden")?n.classList.remove("hidden"):n.classList.add("hidden")
		})
	}),
	[].slice.call(document.querySelectorAll(".toggle-description")).forEach(function(e){
		e.addEventListener("click",function(e){
			var t=e.target.parentNode.querySelector(".description");
			t.classList.contains("hidden")?t.classList.remove("hidden"):t.classList.add("hidden")
		})
	})
});

jQuery(document).ready(function($){
	$('.show-description').on("click", function(){
		$('.description').each(function(){
			$(this).removeClass("hidden");
		})
	});
	$('.hide-description').on("click", function(){
		$('.description').each(function(){
			$(this).addClass("hidden");
		})
	});
});